<?phprequire_once "config.php";class DataBase{    protected $conexion;    protected $db;    public function conectar()    {			if (!function_exists('mysql_connect')) { 			$this->conexion = mysqli_connect(DB_SERVER,DB_SERVER_USERNAME,DB_SERVER_PASSWORD);			if (!$this->conexion ) DIE("Lo sentimos, no se ha podido conectar con MySQL: " . mysqli_error());			$this->db = mysqli_select_db($this->conexion, DB_DATABASE);			if (!$this->db) DIE("Lo sentimos, no se ha podido conectar con la base datos: " . DB_DATABASE);		} else		{		    $this->conexion = mysql_connect(DB_SERVER, DB_SERVER_USERNAME, DB_SERVER_PASSWORD);			if ($this->conexion == 0) DIE("Lo sentimos, no se ha podido conectar con MySQL: " . mysql_error());			$this->db = mysql_select_db(DB_DATABASE, $this->conexion);			if ($this->db == 0) DIE("Lo sentimos, no se ha podido conectar con la base datos: " . DB_DATABASE);			}        return true;    }    public function desconectar()    {        if ($this->conectar->conexion) {			if (!function_exists('mysql_close')) { 				mysqli_close($this->$conexion);			} else {				mysql_close($this->$conexion);			}		        }    }		public function getConexion() {		return $this->conexion;	}		public function getSentence($sql) {		if (!function_exists('mysql_query')) { 			$query = $this->conexion->query($sql);		} else {			mysql_query("SET NAMES 'utf8'");			$query = mysql_query($sql, $this->conexion);		}				return $query;	}		public function getError() {						if (!function_exists('mysql_error')) { 			return mysqli_error($this->conexion);		} else {			return mysql_error($this->conexion);		}	}		}?>